class Rdio.MainController extends Rdio.ApplicationController
  routingKey: 'main'

  index: (params) ->
    @set 'firstName', 'James'
    @set 'lastName', 'Bond'

  @accessor 'fullName', -> "#{@get('firstName')} #{@get('lastName')}"

class Rdio.PlaylistsController extends Rdio.ApplicationController
  routingKey: 'playlists'

  @beforeAction 'fetchPlaylist', only: ['show', 'edit']

  index: (params) ->
    @set('playlists', Rdio.Playlist.get('all'))

  show: (params) ->

  edit: (params) ->

  new: (params) ->
    @set('playlist', new Rdio.Playlist)

  create: ->
    @playlist.save =>
      @redirect(@playlist)

  update: (params) ->
    @redirect(@playlist)

  destroy: (params) ->

  fetchPlaylist: (params) ->
    Rdio.Playlist.find params.id, (error, list) =>
      throw error if error
      @set('playlist', list)

class Rdio.Album extends Batman.Model
  @resourceName: 'albums'

  @persist Rdio.RdioStorage

  @primaryKey: 'key'
  @encode 'name', 'artist', 'icon'
  @encodeTimestamps()

  @hasMany 'tracks'

  @classAccessor 'popular', promise: (deliver) =>
    @request 'getTopCharts', data: {type: "Album", extras: "tracks"}, (error, response) ->
      popular = new Batman.Set
      popular.add(Rdio.Album.createFromJSON(album)) for album in response
      deliver(error, popular)

class Rdio.Playlist extends Batman.Model
  @resourceName: 'playlists'
  @storageKey: 'playlists'

  @persist Batman.RailsStorage

  @encode 'name', 'icon'
  @encodeTimestamps()

  @hasMany 'tracks', autoload: false

class Rdio.Track extends Batman.Model
  @resourceName: 'tracks'
  @storageKey: 'tracks'

  @persist Rdio.RdioStorage

  @primaryKey: 'key'
  @encode 'name', 'duration', 'icon', 'artist'

  @belongsTo 'album', foreignKey: 'album_key'

  play: ->
    Rdio.set 'currentTrack', this

