body {
  margin: 0px;
  font-family: "Helvetica Neue", Arial, Helvetica, sans-serif;
  background-color: #e5e5e5;
}

a {
  text-decoration: none;
  color: #777;
}

ul {
  margin: 0px;
  padding: 0px;
}

// Text


// Layout

.wrapper {
  width: 100%;
  background-color: #e5e5e5;
}

.content {
  width: 400px;
  margin-right: auto;
  margin-left: auto;
}

.playbar { width: 100%; height: 24px; background-color: #222; position: relative; margin: -3px 0 0 0;
	.playhead { text-indent: -9999px; display: block; width: 2px; height: 24px; background: #FFF; position: absolute; top: 0; left: 0; }
}

#player {
  width: 0px;
  height: 0px;
}

.list {
  list-style: none;
  padding: 0;
  a {
    width: 100%;
    display: inline-block;
  }
	img { max-width: 64px;
  }
  li:hover {
    color: white;
    background-color: #999;
  }

  a.button {
    float: right;
    display: block;
    top: 0;
    height: 100%;
    background-color: #777;
    color: #fff;
    text-align: center;
  }
}

.list a {
  padding: 6px 0px;
}

.list img {
  vertical-align: -140%;
}

.main {
  background-color: #FFF;
  margin: 24px 0;
}

.player-controls {
  background-color: #333;
  text-align: center;
  color: #FFF;
  img {
    width: 100%;
  }
}

.player-controls-area {
  background-color: #222;
  text-align: center;
  padding: 24px;
  a {
    height: 40px;
    width: 40px;
    color: white;
    display: inline-block;
    background: image-url('play-icon.png') no-repeat;
    text-indent: -30000px;
  }
}

.player-controls-area:hover {
  //background-color: #2dc578;
}

.playing {
  background: image-url('pause-icon.png') no-repeat !important;
}

.playing:hover {
  //background-color: red !important;
}

// Tracks

.track-list {
  background-color: #FFF;
  h1 {
    padding: 0 24px 24px 24px;
    border-bottom: 1px #eee solid;
  }
}

.tracks {
  margin: 0px;
  padding: 0px;
  img {
    vertical-align: -140%;
    padding-right: 12px;
  }
  a {
    padding: 6px 12px;
  }
}

// Navigation

.navigation {
  background-color: #f2f2f2;
  a {
    padding: 14px;
    margin-top: -30px;
    display: inline-block;
  }
  a:hover {
    background-color: #e6e6e6;
  }
  h1 {
    padding: 20px;
    text-align: center;
    font-size: 24px;
    color: #999;
  }
}

.fl {
  float: left;
}

.fr {
  float: right;
}

.clear {
  clear: both;
}
